import React, { Component } from 'react';
import './UserProfile.css';

export class UserProfile extends Component {
  state={
    profileImg1:'https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png'
  }
  imageHandler1 = (e) => {
    const reader = new FileReader();
    reader.onload = () =>{
      if(reader.readyState === 2){
        this.setState({profileImg: reader.result})
      }
    }
    reader.readAsDataURL(e.target.files[0])
  };
  constructor(props) {
        super(props)
        this.state = {
          board: Array(Math.pow(2,2)).fill(''),
          sqr: {width:'80px',height:'80px'},
          brd: {width:'200px',height:'200px'}
        }
        this.state.board[0]=''
      }

  render(){
    const {profileImg1} = this.state
    return(
      <div className="page">
        <div className="containe">
      <div className="img-holderr">
        <img  alt="" id="img" className="imgg" src="https://media.istockphoto.com/photos/happy-smiling-africanamerican-child-girl-yellow-background-picture-id1168369629?s=612x612" />
        <div><i class="fas fa-envelope-square" style={{margin:'10px'}}></i> <i class="far fa-bell"></i></div> 
      </div> 
      
      <div className="UserInfo">
      <p className="p">Name of worker</p> 
      <div style={{backgroundColor:'lightgray',width:"80%",height:"0px",textAlign:"center",padding:"1px",margin:"0 auto"}}></div>
      <p style={{margin:'20px'}}>Description</p>
      <p style={{margin:'20px'}}>phone number</p>
      <p style={{margin:'5%'}}>Address</p>
      
      <div>
						<img src={profileImg1} alt="" id="img" className="img" />
					</div>
			
      
      <button className="Activatebtn" onClick={() => document.getElementById("board").style.display="block"}>
  Activate works
</button>

       <div style={this.state.brd} className="board"id="board" >
       {this.state.board.map((cell, index) => {
    if (index === 0) {
          return <div
            style={Object.assign({background: `url(https://media.istockphoto.com/photos/happy-smiling-africanamerican-child-girl-yellow-background-picture-id1168369629?s=612x612)`}, this.state.sqr)}
            className="square">
            {cell}
            </div>;
    }
})}
       </div>
      </div>
</div>
</div>
)
  }
}
export default UserProfile;
