import React, {useState} from 'react';
import './UserProfile.css';
import Popup from '../popup/popup';
import MultipleDatePicker from 'react-multiple-datepicker';
import PopUp from "../popupreq/popupreq";
import { useLocation } from 'react-router';

function UserProfile ({}) {
  //const{name,email,city,PhoneNumber}=(location &&location.state) || {};
 /* constructor(props)
      {
        super(props)
        this.state = 
        {
          board: Array(Math.pow(2,2)).fill(''),
          sqr: {width:'80px',height:'80px'},
          brd: {width:'200px',height:'200px'},
          openPopup: false,
        } */
        //const location = useLocation();
        /*this.userInfo = 
        {

        }*/
      //popupreq
       const state = { seen: false};
       const togglePop = () => {useState({seen: !state.seen});};

    const [openPopup, setopenPopup] = useState();
    const handlepopup=()=>{
        setopenPopup({openPopup:true});
      }
     const handleclose=()=>{
       setopenPopup({openPopup:false});
      }
     const  mult=()=>{
        <MultipleDatePicker
        onSubmit={dates => console.log('selected date', dates[0])}/>
      }
  //render(){
    //console.log('this.props.userInfo'+ this.location.userInfo);
    return(
      <div className="page">
        <div className="containe">
      <div className="img-holderr">
        <img  alt="" id="img" className="imgg" src="https://images.unsplash.com/photo-1537511446984-935f663eb1f4?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80" />
        <div><i class="far fa-bell" onClick={togglePop} style={{margin:'10px'}}> </i><i class="fas fa-user-edit" style={{margin:'10px'}} ></i> <i class="fas fa-upload" onClick={handlepopup}></i>
       </div>   
      </div> 
    
      <div className="UserInfo">
         <strong className="p">Name:</strong> 
           <div style={{backgroundColor:'lightgray',width:"80%",height:"0px",textAlign:"center",padding:"1px",margin:"0 auto"}}></div>
            <strong style={{margin:'10px'}}>email:</strong> 
            <strong style={{margin:'10px'}}>city:</strong> 
            <strong style={{margin:'15px'}}>PhoneNumber:</strong>
            <div>
            <h1 className="hh">Select your works Dates </h1>
            {/* <FaCalendarAlt className="icon" /> */}
          <MultipleDatePicker
          onSubmit={dates => console.log('selected date', dates[0])}/>
              </div>
          </div>

         
      </div>
      {openPopup && <Popup toggle={handlepopup} />} 
      {state.seen ? <PopUp toggle={togglePop} /> : null}
</div>
);}
    
   
export default UserProfile;
