import React,{useState} from 'react';
import 'semantic-ui-css/semantic.min.css';
import axios from 'axios';
import emailjs from 'emailjs-com';
import Swal from 'sweetalert2';
import { Form, Input, TextArea, Button } from 'semantic-ui-react';
import './Contact.css'

function Contact(){
  const [name, setname] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setphone] = useState('');
  const [message, setMessage] = useState('');
  const submitRequest = async (e) => {
    e.preventDefault();
    let data ={name:name,email:email,phone:phone,message:message}
    axios.post('/api/forma',data).then(res=>{ 
       if (res.status === 'success'){
      alert("Message Sent."); 
      this.resetForm()
  } else if(res.status === 'fail'){
    alert("Message failed to send.")
}
})}
     
   /* console.log({ name,phone,email, message });
    const response = await fetch("/access", { 
      method: 'POST', 
      headers: { 
          'Content-type': 'application/json'
      }, 
      body: JSON.stringify({email, message}) 
  }); 
    const resData = await response.json(); 
    if (resData.status === 'success'){
      alert("Message Sent."); 
      this.resetForm()
  }else if(resData.status === 'fail'){
      alert("Message failed to send.")
  }
}*/
  return (
  <div className="all">
    <div className="contact3 py-5">
    <div className="card-shadow">
        <img src="https://www.wrappixel.com/demos/ui-kit/wrapkit/assets/images/contact/2.jpg" className="imge"/>
    </div>
    </div>
    <div className="he"> <h1>Quick Contact</h1>
    <form className="for" onSubmit={submitRequest}>
                <div className="col-lg-12">
                  <div className="form-group mt-2">
                    <input className="form-control" type="text" placeholder="name" onChange={e => setname(e.target.value)}/>
                  </div>
                </div>
                <div className="col-lg-12">
                  <div className="form-group mt-2">
                    <input className="form-control" type="text" placeholder="email address" onChange={e => setEmail(e.target.value)}/>
                  </div>
                </div>
                <div className="col-lg-12">
                  <div className="form-group mt-2">
                    <input className="form-control" type="text" placeholder="phone" onChange={e => setphone(e.target.value)}/>
                  </div>
                </div>
                <div className="col-lg-12">
                  <div className="form-group mt-2">
                    <textarea className="form-control" rows="3" placeholder="message"  onChange={e => setMessage(e.target.value)}></textarea>
                  </div>
                </div>
                <div className="col-lg-12">
                  <button type="submit" className="contactbtn" onClick={submitRequest}><span> SUBMIT</span></button>
                </div>
            </form> 
            <div className="ic">
              <div>
                  <div className="mr-6 align-self-center">
                    <img src="https://www.wrappixel.com/demos/ui-kit/wrapkit/assets/images/contact/icon1.png"/>
                  </div>
                  
                    <h6>Address</h6>
                    <p class="">Palestine-<br></br>
                              Ramallah,Nablus,Jenin</p>
                  
                 </div>
                 <div className="ph">
                  <div class="mr-3 align-self-center">
                    <img src="https://www.wrappixel.com/demos/ui-kit/wrapkit/assets/images/contact/icon2.png"/>
                  </div>
                  <div class="">
                    <h6>Phone</h6>
                    <p class="">(+970)0595167258 <br></br>
                    (+970)0599773793
                       </p>
                  </div>
                  </div>
                  <div className="em">
                  <div class="mr-3 align-self-center">
                    <img src="https://www.wrappixel.com/demos/ui-kit/wrapkit/assets/images/contact/icon3.png"/>
                  </div>
                  <div class="">
                    <h6 >Email</h6>
                    <p class="">
                     Homeservices@gmail.com
                    </p>
                  </div>
                  </div>


              </div> 
              </div>
    </div>
   
  );
}
export default Contact;
