import React, { Component } from 'react';
import './UserProfile.css';

export class UserProfile extends Component {
  imageHandler1 = (e) => {
    const reader = new FileReader();
    reader.onload = () =>{
      if(reader.readyState === 2){
        this.setState({profileImg1: reader.result})
      }
    }
    reader.readAsDataURL(e.target.files[0])
  };
  imageHandler2 = (e) => {
    const reader = new FileReader();
    reader.onload = () =>{
      if(reader.readyState === 2){
        this.setState({profileImg2: reader.result})
      }
    }
    reader.readAsDataURL(e.target.files[0])
  };
  imageHandler3 = (e) => {
    const reader = new FileReader();
    reader.onload = () =>{
      if(reader.readyState === 2){
        this.setState({profileImg3: reader.result})
      }
    }
    reader.readAsDataURL(e.target.files[0])
  };
  constructor(props) {
        super(props)
        this.state = {
          profileImg1:'https://image.shutterstock.com/image-vector/camera-upload-icon-600w-1055528567.jpg',
          profileImg2:'https://image.shutterstock.com/image-vector/camera-upload-icon-600w-1055528567.jpg',
          profileImg3:'https://image.shutterstock.com/image-vector/camera-upload-icon-600w-1055528567.jpg',

          board: Array(Math.pow(2,2)).fill(''),
          sqr: {width:'80px',height:'80px'},
          brd: {width:'200px',height:'200px'}
        }
        this.state.board[0]=''
      }

  render(){
    const {profileImg1} = this.state
    const{profileImg2}=this.state
    const{profileImg3}=this.state
    return(
      <div className="page">
        <div className="containe">
          
      <div className="img-holderr">
        <img  alt="" id="img" className="imgg" src="https://images.unsplash.com/photo-1537511446984-935f663eb1f4?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80" />
        <div><i class="fas fa-envelope-square" ></i> <i class="far fa-bell" style={{margin:'10px'}}> </i><i class="fas fa-user-edit" style={{margin:'10px'}} ></i></div> 
      </div> 
      
      <div className="UserInfo">
      <p className="p">Name of worker</p> 
      <div style={{backgroundColor:'lightgray',width:"80%",height:"0px",textAlign:"center",padding:"1px",margin:"0 auto"}}></div>
      <p style={{margin:'10px'}}>Description</p>
      <p style={{margin:'10px'}}>phone number</p>
      <p style={{margin:'15px'}}>Address</p>

      <input type="file" accept="image/*" name="image-upload1" id="input" onChange={this.imageHandler1} />
      <input type="file" accept="image/*" name="image-upload1" style={{margin:'10px'}}id="input" onChange={this.imageHandler2} />
      <input type="file" accept="image/*" name="image-upload1" id="input" onChange={this.imageHandler3} />
          <div className="label">
             <label className="image-upload1" htmlFor="input">
               <i class="fas fa-upload"></i>
             </label>

             <label className="image-upload1">
              <i class="fas fa-save"></i>
             </label>
           </div>
      
          <div>
						<img src={profileImg1} alt="" id="img" className="img1" />
            <img src={profileImg2} alt="" id="img" className="img1"/>
            <img src={profileImg3} alt="" id="img" className="img1"/>
					</div>
			
      

       <div style={this.state.brd} className="board"id="board" >
       {this.state.board.map((cell, index) => {
    if (index === 0) {
          return <div
            style={Object.assign({background: `url(https://media.istockphoto.com/photos/happy-smiling-africanamerican-child-girl-yellow-background-picture-id1168369629?s=612x612)`}, this.state.sqr)}
            className="square">
            {cell}
            </div>;
    }
})}
       </div>
      </div>
</div>
</div>
)
  }
}
export default UserProfile;
