import React, {useState} from 'react';
import './UserProfile.css';
import Popup from '../popup/popup';
import PopUp from "../popupupd/popupupd";
import popup from "../popupreq/popupreq"
import MultipleDatePicker from 'react-multiple-datepicker';
//import { useLocation } from 'react-router';

function UserProfile ({}) {
  //const{name,email,city,PhoneNumber}=(location &&location.state) || {};
 
  /* constructor(props)
      {super(props)
        this.state = { openPopup: false,}
        const location = useLocation();
        this.userInfo = { } } */
        
      //popup Edit
    const [pressed, setPressed] = useState(false);
    const HandlePopUp = () => { setPressed(!pressed) }

      //popup Upload image
    const [openPopup, setopenPopup] = useState(false);
    const handlepopup=()=>{setopenPopup(!openPopup);}

     //popup Requests
    const [openreq, setopenreq] = useState(false);
    const handlepopupreq=()=>{setopenreq(!openreq);}
    

     //try to put icon calender
     const  mult=()=>{<MultipleDatePicker onSubmit={dates => console.log('selected date', dates[0])}/>}
  
     //render(){
     //console.log('this.props.userInfo'+ this.location.userInfo);
     return(
      <div className="page">
        <div className="containe">
          <div className="img-holderr">
             <img  alt="" id="img" className="imgg" src="https://images.unsplash.com/photo-1537511446984-935f663eb1f4?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80" />
        <div><i class="far fa-bell" onClick={handlepopupreq}  style={{margin:'10px'}}> </i><i class="fas fa-user-edit" onClick={HandlePopUp} style={{margin:'10px'}} ></i> <i class="fas fa-upload" onClick={handlepopup}></i> </div>   
           </div> 
    
      <div className="UserInfo">
         <p className="p">Name:</p> 
           <div style={{backgroundColor:'lightgray',width:"80%",height:"0px",textAlign:"center",padding:"1px",margin:"0 auto"}}></div>
            <p style={{margin:'10px'}}>email:</p> 
            <p style={{margin:'10px'}}>city:</p> 
            <p style={{margin:'15px'}}>PhoneNumber:</p>
            <div>
            <h1 className="hh">Select your works Dates </h1>
            {/* <FaCalendarAlt className="icon" /> */}
          <MultipleDatePicker
          onSubmit={dates => console.log('selected date', dates[0])}/>
              </div>
          </div>
      </div>
      {openPopup && <Popup toggle={handlepopup} />} 
      {pressed && <PopUp toggle={HandlePopUp} />}
      {openreq && <popup toggle={handlepopupreq} />}
</div>
);}
    
   
export default UserProfile;
